import { DateTime } from 'luxon'
import {
  BaseModel,
  column,
  belongsTo,
  BelongsTo } from '@ioc:Adonis/Lucid/Orm'
import Usuario from './Usuario'

export default class ApiToken extends BaseModel {
  public static table = 'api_tokens'
  @column({ isPrimary: true })
  public id: number

  @column()
  public id_usuario: number

  @column()
  public name: string

  @column()
  public type: string

  @column()
  public token: string

  @column.dateTime({ autoCreate: true })
  public createdAt: DateTime

  @column.dateTime()
  public expires_at: DateTime

  @belongsTo(() => Usuario,{
    localKey: 'id_usuario',
  })
  public rol: BelongsTo<typeof Usuario>
}

import Env from '@ioc:Adonis/Core/Env'
export default class EmailService {
    sendEmail(emailTo,theSubject,theHTML) {
        const sgMail = require('@sendgrid/mail')
        sgMail.setApiKey(Env.get('SENDGRID_API_KEY'))
        const msg = {
            to: emailTo, // Change to your recipient
            from: Env.get('SENDGRID_FROM_EMAIL'), // Change to your verified sender
            subject: theSubject,
            html: theHTML,
        }
        sgMail
            .send(msg)
            .then(() => {
                console.log('Email sent')
            })
            .catch((error) => {
                console.error(error)
            })
    }
}
